T = {'–∞':'a','–±':'b','–≤':'v','–≥':'g','–¥':'d','–µ':'e','—ë':'yo','–∂':'zh','–∑':'z','–∏':'i','–π':'y','–∫':'k','–ª':'l','–º':'m','–Ω':'n','–æ':'o','–ø':'p','—Ä':'r','—Å':'s','—Ç':'t','—É':'u','—Ñ':'f','—Ö':'h','—Ü':'ts','—á':'ch','—à':'sh','—â':'sch','—ä':'','—ã':'y','—å':'','—ç':'e','—é':'yu','—è':'ya'}
def tr(txt):
    return ''.join([T.get(c,c) for c in txt.lower()])
def slug(txt):
    return slugify(tr(txt))[:120]

# –ò–∫–æ–Ω–∫–∏
IC = {'–∞–∫–∫–∞—É–Ω—Ç—ã':'bi-person-circle','–∫–ª—é—á–∏':'bi-key-fill','–¥–æ–Ω–∞—Ç':'bi-gem','—É—Å–ª—É–≥–∏':'bi-tools','–ø—Ä–µ–¥–º–µ—Ç—ã':'bi-box-seam','–±—É—Å—Ç':'bi-graph-up-arrow','–æ–±—É—á–µ–Ω–∏–µ':'bi-book','–ø—Ä–æ–∫–∞—á–∫–∞':'bi-arrow-up-circle','–∑–æ–ª–æ—Ç–æ':'bi-coin','—Å–µ—Ä–µ–±—Ä–æ':'bi-coin','–º–æ–Ω–µ—Ç—ã':'bi-coin','–∞–ª–º–∞–∑—ã':'bi-gem','–≥–µ–º—ã':'bi-gem','–∫—Ä–∏—Å—Ç–∞–ª–ª—ã':'bi-gem','—Ä—É–±–ª–∏':'bi-currency-ruble','–ø–æ–ø–æ–ª–Ω–µ–Ω–∏–µ':'bi-wallet2','–ø–æ–¥–ø–∏—Å–∫–∞':'bi-star-fill','pass':'bi-award','–ø—Ä–æ–ø—É—Å–∫':'bi-ticket-perforated','—Ä–µ–π–¥—ã':'bi-people-fill','–ø–æ–¥–∑–µ–º–µ–ª—å—è':'bi-door-closed','–∫–≤–µ—Å—Ç—ã':'bi-journal-text','–¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è':'bi-trophy','twitch':'bi-twitch','prime':'bi-star','game pass':'bi-xbox','–æ—Ñ—Ñ–ª–∞–π–Ω':'bi-shield-check','–∫–µ–π—Å—ã':'bi-box','—Å–∫–∏–Ω—ã':'bi-palette','—ç–∫–∏–ø–∏—Ä–æ–≤–∫–∞':'bi-shield-shaded'}
def ic(name):
    nl = name.lower()
    for k,v in IC.items():
        if k in nl: return v
    return 'bi-tag'

def add(name, cats, order):
    g, gc = Game.objects.get_or_create(name=name, defaults={'slug':slug(name),'is_active':True,'order':order,'icon':'bi-controller'})
    cc = 0
    for i, cn in enumerate(cats, 1):
        c, crc = Category.objects.get_or_create(game=g, name=cn, defaults={'slug':slug(cn),'icon':ic(cn),'order':i,'is_active':True})
        cc += crc
    return gc, cc

